import { useEffect, useState } from 'react';
import './App.css';

function App() {

  const [isCountingDown, setIsCountingDown] = useState(false);
  const defaultDate = new Date('2023-10-06T00:00:00');
  const [date, setDate] = useState(defaultDate)
  const [displayTime, setDisplayTime] = useState('00:00');


  const add10Sec = (() => {
    date.setSeconds(date.getSeconds() + 10);
    setDate(date)
    setDisplayTime(String(date.getMinutes()).padStart(2, "0") + ":" + date.getSeconds())
  })

  const add1Sec = (() => {
    date.setSeconds(date.getSeconds() + 1);
    setDate(date)
    setDisplayTime(String(date.getMinutes()).padStart(2, "0") + ":" + date.getSeconds())
  })

  const add1Min = (() => {
    date.setMinutes(date.getMinutes() + 1);
    setDate(date)
    setDisplayTime(String(date.getMinutes()).padStart(2, "0") + ":" + date.getSeconds())
  })

  const resetTimer = (() => {
    setDate(defaultDate);
    setDisplayTime("00:00");
  })


  useEffect(() => {
    const seconds = setInterval(() => {
        updateDateAndDisplayTime();
        checkIfDone(displayTime);

    }, 1000);
    return () => clearInterval(seconds);
  }, [isCountingDown]);

  const updateDateAndDisplayTime = (() => {
    if(isCountingDown === true){
      date.setSeconds(date.getSeconds() - 1);
      setDate(date)
      setDisplayTime(String(date.getMinutes()).padStart(2, "0") + ":" + String(date.getSeconds()).padStart(2, "0"))

    }
  })

  const checkIfDone = ((props) => {
    console.log(props.displayTime)
  })

  const updateIfCountingDown = (() => {
    console.log("HERE")
    if (isCountingDown === false) {
      setIsCountingDown(true);
    } else {
      setIsCountingDown(false)
    }

    console.log(isCountingDown)
  })

  return (
    <div className="App">
      <p>{displayTime}</p>
      <button onClick={updateIfCountingDown}>Start/Stop</button>
      <button onClick={resetTimer}>Reset</button>

      <div></div>

      <button onClick={add1Min}>+ 1 M</button>
      <button onClick={add1Sec}>+ 1 S</button>
      <button onClick={add10Sec}>+ 10 S</button>
    </div>
  );
}

export default App;
