import { useEffect, useState } from 'react';
import './App.css';

function App() {

  const isCountingDown = false;

  const defaultRemainingTime = new Date('2023-10-06T00:00:00');
  const [remainingTime, setRemainingTime] = useState(defaultRemainingTime)
  const [displayTime, setDisplayTime] = useState('00:00');


  const add10Sec = (() => {
    remainingTime.setSeconds(remainingTime.getSeconds() + 10);
    setRemainingTime(remainingTime)
    setDisplayTime(String(remainingTime.getMinutes()).padStart(2, "0") + ":" + remainingTime.getSeconds())
  })

  const add1Sec = (() => {
    remainingTime.setSeconds(remainingTime.getSeconds() + 1);
    setRemainingTime(remainingTime)
    setDisplayTime(String(remainingTime.getMinutes()).padStart(2, "0") + ":" + remainingTime.getSeconds())
  })

  const add1Min = (() => {
    remainingTime.setMinutes(remainingTime.getMinutes() + 1);
    setRemainingTime(remainingTime)
    setDisplayTime(String(remainingTime.getMinutes()).padStart(2, "0") + ":" + remainingTime.getSeconds())
  })

  const resetTimer = (() => {
    setRemainingTime(defaultRemainingTime);
    setDisplayTime("00:00");
  })

  const updateRemainingTime = (() => {

  })


  return (
    <div className="App">
      <p>{displayTime}</p>
      <button>Start/Stop</button>
      <button onClick={resetTimer}>Reset</button>

      <div></div>

      <button onClick={add1Min}>+ 1 M</button>
      <button onClick={add1Sec}>+ 1 S</button>
      <button onClick={add10Sec}>+ 10 S</button>
    </div>
  );
}

export default App;
