import { useState } from 'react';
import './App.css';

function App() {

  const isCountingDown = false;

  const defaultDate = new Date('2023-10-06T00:00:00');
  const [date, setDate] = useState(defaultDate)
  const [displayTime, setDisplayTime] = useState('00:00');

  const add10Sec = (() => {

    date.setSeconds(date.getSeconds() + 10);
    setDate(date)

    setDisplayTime(date.getMinutes() + ":" + date.getSeconds())
  })

  return (
    <div className="App">
      <p>{displayTime}</p>
      <button>Start/Stop</button>

      <div></div>

      <button>+ 1 M</button>
      <button>+ 1 S</button>
      <button onClick={add10Sec}>+ 10 S</button>
    </div>
  );
}

export default App;
